return items.map((item, index) => {
  // Get HTML from HTTP Request
  let html = "";
  if (typeof item.json === 'string') {
    html = item.json;
  } else if (item.json?.data) {
    html = item.json.data;
  } else if (item.binary?.data) {
    html = item.binary.data.toString('utf-8');
  }

  // Get email data from previous Code2 node
  const allItems = $('Code2').all();
  const emailData = allItems[index]?.json || {};

  // Clean HTML
  const cleanHtml = html
    .replace(/<script\b[^<]*(?:(?!<\/script>)<[^<]*)*<\/script>/gi, '')
    .replace(/<style\b[^<]*(?:(?!<\/style>)<[^<]*)*<\/style>/gi, '');

  // Helper to clean text
  const cleanText = (text) => {
    if (!text) return null;
    return text
      .replace(/<[^>]*>/g, ' ')
      .replace(/&nbsp;/g, ' ')
      .replace(/&amp;/g, '&')
      .replace(/&lt;/g, '<')
      .replace(/&gt;/g, '>')
      .replace(/&quot;/g, '"')
      .replace(/\s+/g, ' ')
      .trim()
      .replace(/\s*<[^>]*$/, '');
  };

  // Extract tender details section
  const detailsMatch = cleanHtml.match(/TENDER\s+DESCRIPTION([\s\S]*?)(?:CONTACT\s+INFORMATION|$)/i);
  const fullTenderDetails = detailsMatch ? cleanText(detailsMatch[1]) : "";

  // ==================================================
  // PARSE FROM fullTenderDetails
  // ==================================================

  // PA Ref Number / Tender Reference
  const paRefMatch = fullTenderDetails.match(/(?:Tender Reference|PA Ref Number)\s*:?\s*([A-Z0-9\/-]+)/i);
  const paRefNumber = paRefMatch ? paRefMatch[1].trim() : null;

  // Issued By
  const issuedByMatch = fullTenderDetails.match(/Issued by\s+([^I]+?)(?=\s+Internal\/External|$)/i);
  const issuedBy = issuedByMatch ? issuedByMatch[1].trim() : null;

  // Initial Bond
  const initialBondMatch = fullTenderDetails.match(/Initial Bond\s+(BD\s*[\d,]+)/i);
  const initialBond = initialBondMatch ? initialBondMatch[1].trim() : null;

  // Tender Fees
  const tenderFeesMatch = fullTenderDetails.match(/Tender Fees\s+(BD\s*[\d,]+)/i);
  const tenderFees = tenderFeesMatch ? tenderFeesMatch[1].trim() : null;

  // Contract Duration
  const contractDurationMatch = fullTenderDetails.match(/Contract Duration\s+(\d+\s+(?:Year|Month|Day)s?)/i);
  const contractDuration = contractDurationMatch ? contractDurationMatch[1].trim() : null;

  // Bid Validity
  const bidValidityMatch = fullTenderDetails.match(/Bid Validity[^0-9]*(\d+\s+days)/i);
  const bidValidity = bidValidityMatch ? bidValidityMatch[1].trim() : null;

  // Invitation Method
  const invitationMethodMatch = fullTenderDetails.match(/Invitation Method\s+([^\n]+?)(?=\s+Classification|$)/i);
  const invitationMethod = invitationMethodMatch ? invitationMethodMatch[1].trim() : null;

  // Internal/External
  const internalExternalMatch = fullTenderDetails.match(/Internal\/External\s+(Internal|External)/i);
  const internalExternal = internalExternalMatch ? internalExternalMatch[1].trim() : null;

  // Classification/CPV Codes - FIXED
  const classificationMatch = fullTenderDetails.match(/Classification\/CPV Codes\s+-->\s*([^-]+)\s+-\s+([^-]+?)\s+-->/i);
  const classification = classificationMatch ? `${classificationMatch[1].trim()} - ${classificationMatch[2].trim()}` : null;

  // Publish Date
  const publishDateMatch = fullTenderDetails.match(/Publish Date\s+([A-Z][a-z]+,\s+\d{2}\s+[A-Z][a-z]+\s+\d{4})/i);
  const publishDate = publishDateMatch ? publishDateMatch[1].trim() : null;

  // Purchase Before
  const purchaseBeforeMatch = fullTenderDetails.match(/Purchase Before\s+([A-Z][a-z]+,\s+\d{2}\s+[A-Z][a-z]+\s+\d{4})/i);
  const purchaseBefore = purchaseBeforeMatch ? purchaseBeforeMatch[1].trim() : null;

  // Opening Date
  const openingDateMatch = fullTenderDetails.match(/Opening Date\s+([A-Z][a-z]+,\s+\d{2}\s+[A-Z][a-z]+\s+\d{4})/i);
  const openingDate = openingDateMatch ? openingDateMatch[1].trim() : null;

  // Closing Date
  const closingDateMatch = fullTenderDetails.match(/Closing Date\s+([A-Z][a-z]+,\s+\d{2}\s+[A-Z][a-z]+\s+\d{4})/i);
  const closingDateFromPage = closingDateMatch ? closingDateMatch[1].trim() : null;

  // Mandatory Criteria - FIXED (stop before Arabic)
  const mandatoryCriteriaMatch = fullTenderDetails.match(/Mandatory Criteria:\s*(.*?)(?=\s*,\s*[ÿ•ŸÖÿ±ÿ¨ÿπŸÜŸàÿµŸÅ]|Download|$)/i);
  const mandatoryCriteria = mandatoryCriteriaMatch ? mandatoryCriteriaMatch[1].trim() : null;

  // Tender Description - FIXED (stop before Mandatory or Arabic)
  const tenderDescMatch = fullTenderDetails.match(/Tender Description:\s*(.*?)(?=\s*Mandatory Criteria|,\s*[ÿ•ŸÖÿ±ÿ¨ÿπŸÜŸàÿµŸÅ]|Download|$)/i);
  const tenderDescription = tenderDescMatch ? tenderDescMatch[1].trim() : null;

  // Is Alternate Bid Allowed
  const isAlternateBidAllowed = /Is Alternate Bid Allowed\s+Yes/i.test(fullTenderDetails) ? 'Yes' : 
                                 /Is Alternate Bid Allowed\s+No/i.test(fullTenderDetails) ? 'No' : null;

  // ==================================================
  // PDF DETECTION - IMPROVED
  // ==================================================
  let pdfAvailable = false;
  let pdfDirectLink = null;

  // Check for dynamic download button
  if (html.includes('lnkBtnDwnld') || html.includes('fa-download')) {
    pdfAvailable = true;
  }

  // Try to find direct PDF links (if any)
  const ignorePdfs = ['Vision2030', 'guide', 'help', 'faq', 'terms'];
  const pdfMatch = html.match(/href=["']([^"']*\.pdf[^"']*)["']/i);

  if (pdfMatch && !ignorePdfs.some(ignore => pdfMatch[1].toLowerCase().includes(ignore.toLowerCase()))) {
    pdfDirectLink = pdfMatch[1];
    if (!pdfDirectLink.startsWith('http')) {
      pdfDirectLink = pdfDirectLink.startsWith('/') 
        ? `https://www.tenderboard.gov.bh${pdfDirectLink}`
        : `https://www.tenderboard.gov.bh/${pdfDirectLink}`;
    }
    pdfAvailable = true;
  }

  // ==================================================
  // CONTACT INFO EXTRACTION
  // ==================================================

  // Agency Contact (before "Bahrain Tender Board" section)
  const agencyContactMatch = fullTenderDetails.match(/For further inquiries please contact on:\s*([^\n]+?)\s*(\d{8})/i);
  const agencyContactName = agencyContactMatch ? agencyContactMatch[1].trim() : issuedBy;
  const agencyContactPhone = agencyContactMatch ? `+973 ${agencyContactMatch[2].trim()}` : null;

  // Tender Board contacts
  const allPhones = fullTenderDetails.match(/\+?973[\s\d]{8,}/g) || [];
  const tenderBoardPhones = allPhones.filter(p => !agencyContactPhone || !p.includes(agencyContactPhone.replace(/\D/g, '')));

  const tenderBoardEmail = fullTenderDetails.match(/([a-z0-9._%+-]+@tenderboard\.gov\.bh)/i)?.[1] || null;
  const poBox = fullTenderDetails.match(/P\.O\.\s*Box\s+(\d+)/i)?.[1] || null;

  // ==================================================
  // REQUIREMENTS FLAGS
  // ==================================================
  const requiresCommercialReg = /Commercial Registration Certificate|valid CR/i.test(fullTenderDetails);
  const requiresBahranization = /Bahranization Certificate|employment percentages for Bahraini manpower/i.test(fullTenderDetails);
  const smeEligible = /Small and Medium Enterprises.*10%|SMEs Classification Certificate/i.test(fullTenderDetails);
  const registrationRequired = /register.*e-tendering system/i.test(fullTenderDetails);

  // ==================================================
  // DATE PARSING TO ISO
  // ==================================================
  const parseDate = (dateStr) => {
    if (!dateStr) return null;
    try {
      const months = {
        'JAN': 0, 'FEB': 1, 'MAR': 2, 'APR': 3, 'MAY': 4, 'JUN': 5,
        'JUL': 6, 'AUG': 7, 'SEP': 8, 'OCT': 9, 'NOV': 10, 'DEC': 11
      };
      const parts = dateStr.match(/(\d{2})-([A-Z]{3})-(\d{4})\s+(\d{2}):(\d{2}):(\d{2})/i);
      if (parts) {
        const [, day, month, year, hour, minute, second] = parts;
        const date = new Date(year, months[month.toUpperCase()], day, hour, minute, second);
        return date.toISOString();
      }
    } catch (e) {}
    return dateStr;
  };

  // ==================================================
  // CALCULATE DAYS REMAINING
  // ==================================================
  const getDaysRemaining = (isoDate) => {
    if (!isoDate) return null;
    try {
      const deadline = new Date(isoDate);
      const today = new Date();
      return Math.ceil((deadline - today) / (1000 * 60 * 60 * 24));
    } catch (e) {
      return null;
    }
  };

  const daysRemaining = getDaysRemaining(parseDate(emailData.closingDate));

  // ==================================================
  // RETURN COMPLETE STRUCTURED DATA
  // ==================================================
  return {
    json: {
      // ===== CORE TENDER INFO =====
      timestamp: emailData.timestamp || new Date().toISOString(),
      tenderNumber: emailData.tenderNumber,
      tenderTitle: emailData.tenderTitle,
      paRefNumber: paRefNumber,

      // ===== ISSUING AUTHORITY =====
      issuedBy: issuedBy,
      internalExternal: internalExternal,
      invitationMethod: invitationMethod,

      // ===== FINANCIAL =====
      initialBond: initialBond,
      tenderFees: tenderFees,
      contractDuration: contractDuration,
      bidValidity: bidValidity,

      // ===== DATES (Original Format) =====
      salesStart: emailData.salesStart,
      closingDate: emailData.closingDate,
      publishDate: publishDate,
      purchaseBefore: purchaseBefore,
      openingDate: openingDate,
      closingDateFromPage: closingDateFromPage,

      // ===== DATES (ISO Format for DB) =====
      salesStartISO: parseDate(emailData.salesStart),
      closingDateISO: parseDate(emailData.closingDate),

      // ===== CALCULATED FIELDS =====
      daysRemaining: daysRemaining,

      // ===== CLASSIFICATIONS =====
      classification: classification,
      isAlternateBidAllowed: isAlternateBidAllowed,

      // ===== CONTENT =====
      fullTenderDetails: fullTenderDetails,
      tenderDescription: tenderDescription,
      mandatoryCriteria: mandatoryCriteria,

      // ===== DOCUMENTS =====
      pdfAvailable: pdfAvailable,
      pdfDirectLink: pdfDirectLink,

      // ===== URLS =====
      tenderUrl: emailData.tenderUrl,
      eTenderingUrl: emailData.eTenderingUrl,

      // ===== CONTACT INFO =====
      agencyContacName:  agencyContactName,
       agencyContactPhone: agencyContactPhone,

      tenderBoardContact: {
        email: tenderBoardEmail,
        phones: tenderBoardPhones,
        poBox: poBox ? `P.O. Box ${poBox}` : null
      },

      // ===== REQUIREMENTS =====
      requiresCommercialReg: requiresCommercialReg,
      requiresBahranization: requiresBahranization,
      smeEligible: smeEligible,
      registrationRequired: registrationRequired,

      // ===== EMAIL DATA =====
      emailSubject: emailData.emailSubject,
      emailBody: emailData.emailBody,

      // ===== LEGACY FIELDS (for backward compatibility) =====
      receivedDate: emailData.timestamp,
      submissionDeadline: emailData.closingDate,
      originalUrl: emailData.eTenderingUrl,
      subject: emailData.emailSubject,
      rawBody: emailData.emailBody
    }
  };
});
